{"settings":"{
  //--------------VIM STUFF----------------------//

  "vim.useSystemClipboard": true,
  "vim.cursorStylePerMode.normal": "block",
  "vim.leader": "<Space>",
  "editor.lineNumbers": "relative",
  "vim.handleKeys": {
  },
  "vim.insertModeKeyBindingsNonRecursive": [

  ],
  "vim.visualModeKeyBindingsNonRecursive": [
    // hjkl remap
    {
      "before": ["j"],
      "after": ["h"]
    },
    {
      "before": ["k"],
      "after": ["j"]
    },
    {
      "before": ["i"],
      "after": ["k"]
    },
    {
      "before": ["h"],
      "after": ["i"]
    },

    // start of line
    {
      "before": ["_"],
      "after": ["#"]
    },
    {
      "before": ["#"],
      "after": ["_"]
    }
  ],
  "vim.operatorPendingModeKeyBindingsNonRecursive": [
    // hjkl remap
    {
      "before": ["j"],
      "after": ["h"]
    },
    {
      "before": ["k"],
      "after": ["j"]
    },
    {
      "before": ["i"],
      "after": ["k"]
    },
    {
      "before": ["h"],
      "after": ["i"]
    },
    // start of line
    {
      "before": ["_"],
      "after": ["#"]
    },
    {
      "before": ["#"],
      "after": ["_"]
    },
  ],
  "vim.normalModeKeyBindingsNonRecursive": [
    // hjkl remap
    {
      "before": ["j"],
      "after": ["h"]
    },
    {
      "before": ["k"],
      "after": ["j"]
    },
    {
      "before": ["i"],
      "after": ["k"]
    },
    {
      "before": ["h"],
      "after": ["i"]
    },

    // start of line
    {
      "before": ["_"],
      "after": ["#"]
    },
    {
      "before": ["#"],
      "after": ["_"]
    },

    // vertical movement
    {
      "before": ["<C-d>"],
      "after": ["<C-d>", "z", "z"]
    },
    {
      "before": ["<C-u>"],
      "after": ["<C-u>", "z", "z"]
    },

    // command pallete stuff
    {
      "before": ["leader", "o"],
      "commands": ["workbench.action.quickOpen"]
    },
    {
      "before": ["leader", "c"],
      "commands": ["workbench.action.showCommands"]
    },
    {
      "before": ["leader", "s"],
      "commands": ["workbench.action.gotoSymbol"]
    },
    {
      "before": ["leader", "f"],
      "commands": ["actions.find"]
    },
    // sidebar
    {
      "before": ["leader", "e"],
      "commands": ["workbench.explorer.fileView.focus"]
    },
    {
      "before": ["leader", "g"],
      "commands": ["workbench.panel.chat.view.copilot.focus"]
    }

  ],

  //------------------------NORMAL SETTINGS----------------------------------//

  "workbench.colorTheme": "Default Dark Modern",
  "window.titleBarStyle": "custom",
  "workbench.panel.defaultLocation": "right",
  "workbench.settings.applyToAllProfiles": ["workbench.panel.defaultLocation"],
  "workbench.startupEditor": "none",
  "security.workspace.trust.untrustedFiles": "open",
  "C_Cpp.autocompleteAddParentheses": true,
  "C_Cpp.suggestSnippets": false,
  "cmake.showOptionsMovedNotification": false,
  "git.autofetch": true,
  "git.confirmSync": false,
  "terminal.integrated.cursorStyle": "line",
  "redhat.telemetry.enabled": false,
  // "java.jdt.ls.java.home": "/usr/lib/jvm/java-21-openjdk",
  "editor.minimap.enabled": false,
  "editor.renderWhitespace": "none",
  "git.openRepositoryInParentFolders": "never",
  "github.copilot.enable": {
    "*": false,
    "plaintext": false,
    "markdown": false,
    "scminput": false
  },
  "java.import.gradle.java.home": "/usr/lib/jvm/java-21-openjdk",
  "cmake.configureOnOpen": true,
  "terminal.integrated.fontFamily": "\"Hack Nerd Font\"",
  "editor.fontFamily": "\"Hack Nerd Font\"",
  "editor.fontSize": 16,
  "terminal.integrated.fontSize": 16,
  "workbench.enableExperiments": false,
  "workbench.settings.enableNaturalLanguageSearch": false,
  "extensions.autoCheckUpdates": false,
  "update.showReleaseNotes": false,
  "explorer.confirmDelete": false,
  "window.customTitleBarVisibility": "auto",
  "workbench.sideBar.location": "right",
  "statusbarerror.wholeLine.show": true,
  "statusbarerror.statusbar.show": false,
  "todo-tree.highlights.customHighlight": {
    "TODO": {
        "type": "text-and-comment",
        "foreground": "#000000",
        "background": "#965F0E"
    },
    "FIXME": {
        "gutterIcon": true
    },
    "BUG": {
      "icon": "bug"
    },
    "HACK": {
      "icon": "tools"
    },
    "XXX": {
      "icon": "x"
    },
    "[ ]": {
      "icon": "issue-draft"
    },
    "[x]": {
      "icon": "issue-closed"
    }
  },
  "github.copilot.editor.enableAutoCompletions": true,
  "cmake.pinnedCommands": [
    "workbench.action.tasks.configureTaskRunner",
    "workbench.action.tasks.runTask"
  ],
  "dart.debugExternalPackageLibraries": false,
  "dart.debugSdkLibraries": false,
  "breadcrumbs.enabled": false,




}"}
